import router from "@/router";
import axios from "axios";
export default {
  name: "newCategoryView",

  data() {
    return {
      newCategory: {
        name: '',
        image1: null,
        content: ''
      },
      image: null,
      dflt: 'data:image/jpeg;base64,'
    };
  },

  methods: {
    createNewCategory() {
      let formdata = new FormData();

      if (this.newCategory.image1 !== null) {
        formdata.append('image1', this.newCategory.image1);
      }

      formdata.append('name', this.newCategory.name);
      formdata.append('content', this.newCategory.content);
      axios.post('http://127.0.0.1:8000/api/category', formdata, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem('token'),
          'Content-Type': "multipart/form-data; charset=utf-8"
        }
      }).then(response => {
        let timerInterval;
        this.$swal.fire({
          toast: true,
          text: response.data,
          timer: 1100,
          icon: 'success',
          position: 'top-end',
          timerProgressBar: true,
          showConfirmButton: false,
          willClose: () => {
            clearInterval(timerInterval);
          }
        });
        setTimeout(function () {
          router.push("/category");
        }, 1600);
      }).catch(error => {
        let timerInterval;
        this.$swal.fire({
          text: error.response.data.message,
          timer: 1000,
          toast: true,
          icon: 'error',
          position: 'top-end',
          timerProgressBar: true,
          showConfirmButton: false,
          willClose: () => {
            clearInterval(timerInterval);
          }
        });
      });
    },

